
/**
 * Module dependencies.
 */

var videoExists = require('../')
var should = require('should')

/**
 * Tests.
 */

describe('test known valid id\'s', function() {
  var validIds = [
      '9bZkp7q19f0'
    , 'kffacxfA7G4'
    , 't4H_Zoh7G5A'
    , '_OBlgSz8sSM'
    , 'uelHwf8o7_U'
  ]

  validIds.forEach(function(id) {
    it('video with id ' + id + ' should return true', function(done) {
      videoExists(id, function(exists) {
        exists.should.be.true
        done()
      })
    })
  })
})

describe('test known invalid id\'s', function() {
  var invalidIds = [
      'GyqXzm5hH50'
    , 'youtubeaaaa'
    , 'googlebbbbb'
    , 'aaaa'
    , 'bbbb'
  ]

  invalidIds.forEach(function(id) {
    it('video with id ' + id + ' should return false', function(done) {
      videoExists(id, function(exists) {
        exists.should.be.false
        done()
      })
    })
  })
})
